INSERT INTO `line` (`id`, `name`, `path`, `color`) VALUES
(1, 'Кировско-Выборгская', 'kirovsko-vyborgskaya', '#d60d0d'),
(2, 'Московско-Петроградская', 'moskovsko-petrogradskaya', '#6e5cff'),
(3, 'Невско-Василеостровская', 'nevsko-vasileostrovskaya', '#42c70d'),
(4, 'Правобережная', 'pravoberezhnaya', '#dc871d'),
(5, 'Фрунзенско-Приморская', 'frunzensko-primorskaya', '#821bd0');
INSERT INTO `station` (`path`, `name`, `position`, `foundation_year`, `description`, `line_id`) VALUES
('admiralteyskaya', 'Адмиралтейская', 6, 2011, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Адмиралтейская — колонно-стеновая трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в самом центре Санкт-Петербурга на Кирпичном переулке между Малой и Большой Морскими улицами, недалеко от начала Невского проспекта, Дворцовой площади и Адмиралтейства.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по Адмиралтейству, расположенному в полукилометре от станции (изначально выход со станции планировался намного ближе к Адмиралтейству).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Константинов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nАдмиралтейская была возведена в конструкциях вместе со Спортивной к 1997 году, однако на достройку и открытие собственного выхода Адмиралтейской тогда не хватило денег, и открытие станции задержалось аж на 14 лет. При строительстве выхода было разобрано и заново отстроено (с сохранением облика фасада) здание, в первый этаж которого встроен вход-выход метро. Ранее это был жилой дом, но теперь здесь будет торгово-развлекательный бизнес-центр.\n\r\n    </p>\r\n    <p>\r\n      \nСтанция Адмиралтейская могла бы быть удобной пересадкой на 3-ю, Невско-Василеостровскую линию. Тоннели 3-й и 5-й линий в этом месте проходят практически параллельно на небольшом расстоянии. Однако в Петербургском метрострое нет опыта встраивания станций на действующих участках без перекрытия движения (или хотя бы встраивания камер съездов для подключения временных обходных тоннелей), поэтому вторая станция не была построена, а также она была убрана из планов строительства метро на ближайшие 15 лет.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции посвящено морской тематике. Путевые стены облицованы голубовато-серым мрамором, колонны облицованы мрамором «газган» тёплых светлых оттенков, а также украшены декоративными алюминиевыми полуколоннами, выкрашенными в чёрный цвет, с цоколями и капителями из полированной латуни. Полы выложены серым «мансуровским» гранитом со вставками гранита тона «охра», красного гранита «Кашина гора» и синего камня. Стены станции украшены четырьмя мозаичными панно морской и исторической тематики и барельефами великих русских адмиралов и флотоводцев.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('akademicheskaya', 'Академическая', 3, 1975, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Академическая — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северо-востоке города в Калининском районе у перекрёстка проспекта Науки и Гражданского проспекта.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в этом районе города многие улицы названы именами деятелей науки и академиков, а также сосредоточено большое число НИИ и учреждений Российской Академии Наук.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Г. Хильченко, А. С. Гецкин, В. П. Шувалова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nОтделка станции довольно простая и при этом необычная: полукруглые арки между колоннами отделаны блестящим металлом (тем самым станция напоминает московскую «Маяковскую»). Путевые стены Академической облицованы белым известняком, полы выложены серым гранитом. Раньше станция освещалась простыми, но необычными светильниками, удачно вписывавшимися в интерьер: U-образными люминисцентными трубками, отдалённо напоминающими формой проходы между колоннами. К сожалению, эта деталь оформления была безвозвратно утрачена в первой половине 2004 года: светильники были заменены на обычные люминисцентные лампы в типовых плафонах офисного типа, чем первоначальный интерьер станции был несколько испорчен.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение трёх лет (с 1975 до 1978) Академическая была конечной станцией.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт первой линии.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('avtovo', 'Автово', 17, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Автово — колонная трёхпролётная станция мелкого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-западной части города в Кировском районе на проспекте Стачек недалеко от перекрёстка с Автовской улицей.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию района Автово.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Е. Н. Левинсон, А. А. Грушке.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nАвтово — конечная станция первого участка первой линии Ленинградского метрополитена.\n\r\n    </p>\r\n    <p>\r\n      \nЭто одна из красивейших станций первой очереди. Колонны в центральной части станции облицованы декоративным стеклом. Свет, преломляясь в облицовке колонн, должен создавать эффект свечения изнутри. Правда, сильно портит впечатление то, что половина люстр выключена с целью экономии. А после замены ламп накаливания, дававших резкий чёткий свет, на матовые энергосберегающие лампы с рассеянным светом эффект и вовсе пропал.\n\r\n    </p>\r\n    <p>\r\n      \nТема художественного оформления станции — оборона Ленинграда. Люстры, светильники, орнаменты решёток украшены дубовыми листьями, позолоченными копьями, мечами и щитами — традиционными символами воинской доблести. Торцевую стену центрального зала украшает мозаичное панно «Миру мир».\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт первой линии.\n\r\n    </p>\r\n    <p>\r\n      \nС оборотных путей, находящихся за станцией, начинается ветка в электродепо ТЧ-1 «Автово», частично обслуживающее Кировско-Выборгскую линию и ТЧ-2 «Дачное», где выполняются работы по ремонту подвижного состава.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('baltiyskaya', 'Балтийская', 14, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Балтийская — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится у перекрёстка южной набережной Обводного канала с Митрофаньевским шоссе.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: станция находится рядом с Балтийским железнодорожным вокзалом.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — М. К. Бенуа, А. И. Кубасов, Ф. Ф. Олейник.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nБалтийская — одна из самых скромных станций первой очереди. Стены и колонны станции, облицованные серо-голубым мрамором, и светло-голубой рисунок потолка как бы создают иллюзию морского простора. Торец станции украшен флорентийским мозаичным панно «1917 год», посвящённым революционерам-балтийцам.\n\r\n    </p>\r\n    <p>\r\n      \nИнтересный факт: на большинтве станций первой очереди декоративные технологические двери украшены копьями, поверх которых установлены цифры «1955» — год открытия станции; на Балтийской же вместо года открытия двери украшены стилизованным изображением якоря.\n\r\n    </p>\r\n    <p>\r\n      \nБалтийская была единственной станцией двух вокзалов: здание наземного вестибюля станции пристроено к Балтийскому вокзалу, а невдалеке находился Варшавский вокзал. Несколько лет назад Варшавский вокзал был переделан в торгово-развлекательный комплекс.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('buharestskaya', 'Бухарестская', 11, 2012, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Бухарестская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-восточной части города во Фрунзенcком районе на перекрёстке Бухарестской улицы с улицей Салова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по улице, под которой расположена станция (название очень неудачно для идентификации расположения станции, поскольку 5 станций Фрунзенского радиуса, идущие подряд, все расположены под Бухарестской улицей, причём, эта станция не является ни первой, ни последней, ни средней).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Д. А. Бойцов, О. В. Рязнацева, М. Ю. Мартынова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПо первоначальному проекту Бухарестскую начинали строить односводчатой, но из-за отказа от строительства в Петербургском метрополитене новых односводчатых станций тот проект был изменён уже после начала строительства, в результате чего получилась ещё одна пилонная трёхсводчатая станция заложения уменьшенного размера, построенная по новому типовому петербургскому проекту, часто применяемому в последние годы (конструкции станций Волковская, Обводный канал и Спасская построены по тому же проекту), из-за чего выглядит довольно тесной и полумрачной.\n\r\n    </p>\r\n    <p>\r\n      \nСтены станции облицованы мрамором и гранитом светлых тонов со вставками красного мрамора около проходов между пилонами, полы выложены серо-коричневым и чёрным гранитом. Художественное оформление станции — мозаики и фриз природной тематики (автор — художник Александр Быстров, изготовивший мозаики для всех новых станций Петербургского метрополитена в последние годы).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('chernyshevskaya', 'Чернышевская', 9, 1958, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Чернышевская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в северной части Центрального района Санкт-Петербурга в конце проспекта Чернышевского у перекрёстка с Кирочной улицей.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: станция находится на проспекте Чернышевского.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. В. Жук, С. Г. Майофис.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтроительство Чернышевской велось в хрущёвские времена борьбы с излишествами в проектировании и строительстве, поэтому художественное оформление станции минималистично. На Чернышевской нет ни барельефов, ни лепнины, ни мозаик, ни витражей. При проектировании даже отказались от люстр и светильников, впервые в Ленинградском метрополитене на всей станции было установлено закарнизное освещение. Пилоны станции облицованы серым мрамором, путевые стены — белой кафельной плиткой. Полы центрального зала станции были выложены чёрной и белой плиткой из искусственного камня, а платформы были покрыты асфальтом, который был заменён на гранит в первом десятилетии 2000-х годов.\n\r\n    </p>\r\n    <p>\r\n      \nВ 2002–03 годах на станции вёлся затяжной ремонт облицовки путевых стен, из-за которого она в течение полутора лет выглядела очень непривлекательно. Ремонт был завершен к дням празднования 300-летия Санкт-Петербурга, но результаты его продержались недолго. Спустя лишь несколько лет на станции была проведена повторная замена облицовки.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('chiornaya-rechka', 'Чёрная речка', 6, 1982, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Чёрная речка — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в северо-западной части города в Приморском районе у перекрёстка улиц Савушкина и Академика Крылова с набережной Чёрной речки (исторический район «Новая деревня»).\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по Чёрной речке, известному месту в истории Санкт-Петербурга.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Г. Н. Булдаков, В. Н. Щербин, А. П. Волкова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nНедалеко от места, где сейчас расположена станция метро (примерно на середине пути к Пионерской) в 1837 году на дуэли был смертельно ранен великий русский поэт А. С. Пушкин. Оформление станции посвящено этому трагическому событию.\n\r\n    </p>\r\n    <p>\r\n      \nПамятник Пушкину (скульптор — М. К. Аникушин) установлен в конце зала; это не единственный памятник Пушкину в метро, ещё один памятник установлен на Пушкинской. Путевые стены облицованы чёрным лабрадоритом с дополнительной мраморной полосой, глухой торец облицован белым мрамором. Пол выложен серым гранитом с несколькими большими квадратными вставками из чёрного доломита.\n\r\n    </p>\r\n    <p>\r\n      \nСтанция освещена несколькими шикарными люстрами огромного размера; используются люминисцентные лампы, дающие белый свет слегка тёплого оттенка.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('chkalovskaya', 'Чкаловская', 4, 1997, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Чкаловская — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в Петроградском районе Санкт-Петербурга на пересечении Чкаловского проспекта с Большой Зелениной улицей.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в честь лётчика В. П. Чкалова, именем которого назван проспект, проходящий мимо станции.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. С. Волонсевич, А. С. Константинов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nХудожественное оформление станции посвящено авиации. Пол посадочной платформы был оформлен как взлётно-посадочная полоса аэродрома с соответствующей разметкой. На станции были указатели с алюминиевыми стрелочками, выглядящими так же, как художественные стрелочки на полу платформы. Светильники над путями оформлены в стиле конструктивных элементов самолётов или вертолётов времён Великой Отечественной войны, а светильники у прохода к эскалаторам выполнены в виде самолётных пропеллеров. Торец зала станции украшает витраж с изображением человека, плавно переходящего в неопознанный летательный аппарат.\n\r\n    </p>\r\n    <p>\r\n      \nОтличительной особенностью этой станции является то, что в наклонном ходе светильники расположены не на балюстраде эскалаторов, а на стенах тоннеля.\n\r\n    </p>\r\n    <p>\r\n      \nОгорчает, что со временем станция преобразилась до неузнаваемости по сравнению с тем, как она выглядела в первые 15 лет после открытия. Например, были утрачены металлические буквы с названием станции на путевых стенах; перспектива станции, выглядящая как широкая взлётно-посадочная полоса со стрелочками разметки, перекрыта многочисленными «ИнфоСОСами», типовыми столбами-указателями в стиле “Троицкий мост” и торговыми ларьками, установленными поверх декоративной “разметки”; а оригинальные информационные указатели (со стрелочками, как на полу платформы) были и вовсе выпилены в прямом смысле этого слова.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('deviatkino', 'Девяткино', 1, 1978, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Девяткино — наземная станция, крытая, с боковыми платформами.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к северо-востоку от Санкт-Петербурга в посёлке Мурино Ленинградской области.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по названию железнодорожной станции Девяткино, между путями которой находится станция метро.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, К. Н. Афонская, И. Е. Сергеева, А. В. Квятковский, Н. И. Згодько.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nДевяткино — первая станция метро в России, расположенная в деревне за административными границами города (впоследствии такие станции появились и в Москве). При этом она является самой северной станцией среди российских метрополитенов, а до открытия метро в Хельсинки была ещё и самой северной в Европе.\n\r\n    </p>\r\n    <p>\r\n      \nПлатформы станции совмещены с платформами пригородных поездов (станция метро находится между путями железнодорожной станции, а не сбоку от них), что очень удобно при пересадке из метро в пригородные поезда и обратно.\n\r\n    </p>\r\n    <p>\r\n      \nЗа станцией находится электродепо ТЧ-4 «Северное», частично обслуживающее Кировско-Выборгскую линию.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('dostoyevskaya', 'Достоевская', 2, 1991, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Достоевская — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга на Владимирской площади в месте слияния Владимирского и Загородного проспектов.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в честь писателя Ф. М. Достоевского, который жил в этом районе (недалеко от станции находится музей-квартира Достоевского).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. В. Жук, А. Д. Токмань.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nХудожественное оформление станции посвящено Санкт-Петербургу XIX века, в котором жил и творил великий писатель. Колонны облицованы серым гранитом, путевые стены — светлым известняком, пол выложен серым гранитом. На колоннах висят фонари в стиле XIX века, некоторые проходы между колоннами декорированы коваными решётками.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('elektrosila', 'Электросила', 14, 1961, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Электросила — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в южной части города в Московском районе на Московском проспекте недалеко от перекрёстка с улицей Решетникова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией находится завод Электросила, бывшее одним из крупнейших предприятий энергомашиностроительной отрасли в СССР.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Г. М. Вланин, С. И. Евдокимов, Н. В. Устинович.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция завершает серию из семи пилонных станций на второй линии, идущих подряд.\n\r\n    </p>\r\n    <p>\r\n      \nПилоны со стороны центрального зала просто покрашены, со стороны проходов — облицованы серым мрамором; пол центрального зала выложен красным гранитом. Стены боковых залов были отделаны белой кафельной плиткой, которая к 2011 году была заменена на керамогранит, а асфальтовое покрытие полов было заменено на гранитное серого цвета.\n\r\n    </p>\r\n    <p>\r\n      \nТема интерьера станции — развитие энергетики в Советском Союзе. Торец центрального зала украшает керамическое панно «Электрификация СССР» с известным изречением Ленина: «Коммунизм — это есть советская власть плюс электрификация всей страны».\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('frunzenskaya', 'Фрунзенская', 12, 1961, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Фрунзенская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к югу от центра города в Адмиралтейском районе на Московском проспекте недалеко от набережной Обводного канала.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией в советские годы располагался один из крупнейших городских универмагов «Фрунзенский», который, в свою очередь, получил название в честь советского государственного и военного деятеля М. В. Фрунзе (в настоящее время название станции не привязано к местности, а Фрунзенский район города начинается в нескольких километрах от станции).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Б. Н. Журавлёв.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nФрунзенская — ничем не примечательная станция пилонного типа в связи с тем, что проектировалась и строилась в хрущёвские времена борьбы с архитектурными излишествами. Цоколь пилонов и путевые стены облицованы скромным белым мрамором (раньше на стенах была белая керамическая плитка), верхняя часть просто окрашена в белый цвет. Пол центрального зала выложен серым гранитом с чёрными полосами; в боковых залах раньше был уложен асфальт, позже заменённый на гранит.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('gorkovskaya', 'Горьковская', 8, 1963, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Горьковская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в южной части Петроградского района в Александровском парке рядом с Кронверкским проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по расположению в начале проспекта Максима Горького, на котором долгое время жил писатель (сейчас проспект называется Кронверкским, а название станции не привязано к местности).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — С. И. Евдокимов, Н. И. Башнин, А. П. Изоитко, С. Л. Михайлов, Е. И. Травников.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтроительство Горьковской велось в хрущёвские времена борьбы с излишествами в проектировании и строительстве, поэтому художественное оформление станции минималистично.\n\r\n    </p>\r\n    <p>\r\n      \nПилоны облицованы неполированным известняком. Путевые стены изначально были отделаны белой керамической плиткой, которая во время ремонта в 2009 году была заменена на керамогранит бежевого цвета. Пол выложен серым гранитом (в боковых залах раньше был уложен асфальт). Торец укороченного центрального зала украшает барельеф Горького.\n\r\n    </p>\r\n    <p>\r\n      \nГорьковская оказалась последней станцией пилонного типа среди “старых” станций. После 1963 года в течение 45 лет до 2008 года в Ленинграде и Петербурге не было построено ни одной пилонной станции. Эта тенденция была нарушена в 2008 году с открытием Волковской.\n\r\n    </p>\r\n    <p>\r\n      \nПервый наземный вестибюль станции был построен по типовому проекту. Толщина стен купола была больше метра, он был способен выдержать прямое попадание бомбы. В 2009 году было открыто новое здание вестибюля, построенное на месте снесённого старого. Он уже не обладает такими защитными функциями, зато намного просторнее старого. По задумке архитекторов, новый вестибюль должен быть похож на футуристическую летающую тарелку, а внутри его купол украшен художественной инсталляцией, которая в народе получила прозвище «коровье вымя».\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('gostiny-dvor', 'Гостиный Двор', 3, 1967, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Гостиный Двор — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в самом центре Петербурга под Невским проспектом у Садовой улицы и канала Грибоедова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по центральному городскому универмагу, в который встроен выход со станции.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — С. Г. Майофис, Э. С. Белят, В. Э. Шевеленко.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nГостиный Двор — самая напряженная станция 3-й линии и самый загруженный пересадочный узел Петербургского метрополитена.\n\r\n    </p>\r\n    <p>\r\n      \nГостиный Двор — станция с двумя выходами: один выход ведёт прямо в одноименный универмаг, второй — на канал Грибоедова (используется совместно со станцией Невский проспект). Эта станция — первая в Петербурге с четырьмя эскалаторами в одном наклонном ходе.\n\r\n    </p>\r\n    <p>\r\n      \nСтены облицованы светлым мрамором, на них установлены серебристые металлические карнизы. Пол выложен тёмно-серым гранитом.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('grazhdanskiy-pr', 'Гражданский проспект', 2, 1978, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Гражданский проспект — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северо-восточной окраине города в Калининском районе у перекрёстка Гражданского проспекта с проспектом Просвещения.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: северная часть линии проходит под Гражданским проспектом (название не очень удобно для локализации станции на местности, ведь под Гражданским проспектом находится и соседняя Академическая, а проспект имеет длину около 6 километров).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Г. Н. Булдаков, Л. Е. Кисельгоф.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция Гражданский проспект оформлена материалами жёлтых оттенков: колонны и путевые стены облицованы желтовато-бежевым мрамором, светильники, облицовка торцевой стены и ригели между колоннами отделаны анодированным алюминием золотистого цвета, пол выложен серым и красным гранитом. Над проходами между колоннами установлены светильники-бра, которых раньше было по пять штук, но примерно в 2005 году части светильников были демонтированы, и теперь их по три. Глухой торец станции украшает огромный герб СССР.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('kirovskiy-zavod', 'Кировский завод', 16, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Кировский завод — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-западной части города в Кировском районе на проспекте Стачек рядом с перекрёстком с улицей Васи Алексеева.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией находится Кировский завод, бывший одним из крупнейших заводов военной и машиностроительной промышленности в СССР.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. К. Андреев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция украшена барельефами индустриальной тематики, символически изображающими продукцию, выпускавшуюся на Кировском заводе, что по замыслу архитектора должно напоминать об успехах советской индустриализации. В торце центрального зала установлен бюст В. И. Ленина, вождя мирового пролетариата.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены и колонны станции облицованы серым мрамором. Пол центрального выложен красным гранитом различных оттенков в шахматном порядке и обрамлён чёрными и белыми полосами. Пол боковых залов выложены серым гранитом, ранее здесь был уложен асфальт.\n\r\n    </p>\r\n    <p>\r\n      \nОгромное здание наземного вестибюля станции с массивными колоннами и широкой лестницей напоминает древнегреческий храм.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('komendantskiy-pr', 'Комендантский проспект', 1, 2005, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Комендантский проспект — колонно-стеновая трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в северо-западной части города в Приморском районе в начале Комендантского проспекта у одноимённой площади.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённому проспекту.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Константинов, Н. В. Ромашкин-Тиманов, В. Г. Хильченко, Ю. Ю. Подервянская, Т. Р. Баранова, М. В. Павлова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nОтделка станции нетипична: впервые в Петербургском метро использована облицовка цветными металлопластиковыми панелями голубого, бежевого и тёмно-синего цветов (со слов тогдашнего губернатора, «как в бане»). На путевых стенах были изначально установлены большие панели с названием станции и жёлтым квадратиком, но эта декорация была демонтирована при приведении всех станций к единообразному виду с наклеиванием фиолетовой полосы.\n\r\n    </p>\r\n    <p>\r\n      \nТема художественного оформления станции — история русской авиации и воздухоплавания. На простенках установлено 18 мозаичных медальонов в честь 60-летия победы в Великой отечественной войне, изображающих самолёты военных лет и портреты знаменитых авиаторов (художник — А. К. Быстров).\n\r\n    </p>\r\n    <p>\r\n      \nНаземного вестибюля станция не имеет. Вход устроен через подземный переход, все выходы из которого в течение долгого времени находились с одной и той же стороны Комендантского проспекта (спустя несколько лет трасса проспекта была немного скорректирована, чтобы пролегать между выходами из перехода).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('krestovskiy-ostrov', 'Крестовский остров', 3, 1999, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Крестовский остров — колонно-стеновая трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в северо-западной части города на Крестовском острове на Рюхиной улице, недалеко от второго Елагина моста — одного из входов в ЦПКиО.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по месту расположения станции.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Е. М. Рапопорт, Г. А. Васильев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nКрестовский остров — первая станция метро в России, на которой зеркала использованы в качестве декоративного оформления, что создает иллюзию, что станция в два раза длиннее.\n\r\n    </p>\r\n    <p>\r\n      \nУ станции есть одна конструктивная особенность — это единственная станция колонного типа с полностью железобетонными, а не со стальными колоннами. Из-за этого колонны сделаны более толстыми. При этом некоторые проходы между колоннами заменены на простенки, украшенные мозаиками.\n\r\n    </p>\r\n    <p>\r\n      \nИз-за задержек во время строительства наземного вестибюля станция была открыта уже после открытия участка Чкаловская — Старая деревня, некоторое время поезда следовали мимо нее транзитом. Мозаичные украшения на колоннах появились еще позже — весной 2001 года.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены облицованы травертином, арки, колонны и простенки — известняком. Простенки декорированы мозаиками, символизирующими реки и острова в дельте Невы. Со стороны центрального зала — реки, со стороны боковых залов — острова. Авторы мозаик — художники С. Н. Репин, В. В. Сухов, И. Г. Уралов, Н. П. Фомин.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('kupchino', 'Купчино', 18, 1972, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Купчино — наземная станция, крытая, с боковыми платформами.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на южной окраине города в Московском районе (а не во Фрунзенском, вопреки распространённому заблуждению) на Витебском проспекте к югу от Дунайского.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в связи с расположением вблизи к крупному жилому району Купчино (но на самом деле Купчино начинается восточнее станции, простирается на несколько километров на север и должно обслуживаться преимущественно станциями Фрунзенского радиуса).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — К. Н. Афонская, А. С. Гецкин, И. Е. Сергеева, С. С. Костенко.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nКупчино — старейшая в Ленинграде наземная станция из работающих до сих пор (первой наземной станцией была Дачное, но её закрыли в 1977 году). Наземный вестибюль — один из самых некрасивых среди всех станций Петербургского метрополитена.\n\r\n    </p>\r\n    <p>\r\n      \nСтанционные стены облицованы травертином (изначально была кафельная плитка). Пол выложен серым гранитом (изначально был уложен асфальт).\n\r\n    </p>\r\n    <p>\r\n      \nЗа станцией находится электродепо ТЧ-3 «Московское», обслуживающее Фрунзенско-Приморскую линию (до 2013 года оно обслуживало Московско-Петроградскую линию).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('ladozhskaya', 'Ладожская', 6, 1985, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Ладожская — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в восточной части города в Красногвардейском районе на Заневском проспекте недалеко от перекрёстка с проспектом Энергетиков, рядом с Ладожским железнодорожным вокзалом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по расположению рядом с Ладожским вокзалом (несмотря на то, что станция метро была построена на 18 лет раньше, железнодорожный вокзал тогда уже был в генеральном плане).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Н. Есиновский, С. Н. Кривоносов, Л. А. Филиппова, Ю. А. Рыжов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nТематика архитектурного оформления станции посвящена «Дороге Жизни», которая проходила по Ладожскому озеру. Светильники расположены не у потолка, как обычно, а в виде столбов на платформе, которые напоминают свечи или кресты на кладбище, между которыми проходит “дорога”, заканчивающаяся у торцевой стены с памятным знаком «1941 — ДОРОГА ЖИЗНИ — 1944».\n\r\n    </p>\r\n    <p>\r\n      \nВопреки распространённому заблуждению, Дорога Жизни никогда не проходила через окрестности станции, однако пригородное направление пассажирских перевозок Ладожского вокзала охватывает исторические железнодорожные станции, связанные с ней.\n\r\n    </p>\r\n    <p>\r\n      \nВ 2006 году на станции было заменено освещение на ярко-оранжевое натриевое. В 2010 году вдобавок на стены станции была наклеена оранжевая полоса с названием станции, затем на каждый светильник «столб-крест» было установлено по две камеры видеонаблюдения (что моментально разрушило их художественный облик), а по оси станции были расставлены аж три «ИнфоСОСа» с телевизорами, торговые киоски, рекламные стенды и допольнительне столбов для указателей. В результате этой модернизации станция сменила свой исходный мрачный печальный облик на комично-несуразный, поэтому далее для её показа используется исторический набор фотографий вместо современных (2010 год).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('leninskiy-pr', 'Ленинский проспект', 18, 1977, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Ленинский проспект — колонная трёхпролётная станция мелкого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на южной окраине города в Кировском районе на пересечении Ленинского проспекта и бульвара Новаторов.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в этом месте Кировско-Выборгская линия пересекает Ленинский проспект.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, Е. И. Валь.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЛенинский проспект — одна из двух станций “московского типа” построенная по типовому проекту из сборных железобетонных конструкций, так называемая “новая сороконожка” с 26 парами колонн. В Москве таких станций — десятки, в Петербурге же всего две.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции минималистично, что характерно для большинства станций, построенных по такому типовому проекту. Квадратные колонны, расширяющиеся кверху, облицованы красным гранитом, путевые стены отделаны белым неполированным мрамором. Пол выложен серыми и красными гранитными плитами множества оттенков и размеров в абсолютно хаотичном порядке.\n\r\n    </p>\r\n    <p>\r\n      \nНаземного вестибюля у станции нет, вход осуществляется через подземные переходы на Ленинском проспекте и на бульваре Новаторов.\n\r\n    </p>\r\n    <p>\r\n      \nСтанция Ленинский проспект заменила собой временную наземную станцию Дачное, которая была закрыта и затем разобрана.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('lesnaya', 'Лесная', 6, 1975, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Лесная — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на Кантемировской улице недалеко от перекрёстка с Лесным проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому району Лесное (который, впрочем, начинается севернее станции и исторически простирался вплоть до окрестностей Политехнической); также в километре к северу от станции находится Лесотехническая академия имени С. М. Кирова.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. И. Прибульский, В. В. Ганкевич.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nОтделка станции довольно необычна по сравнению с другими станциями подобного типа: стены станции облицованы выпуклой зеленой плиткой, символизирующей как бы густой лес. Торец станции освещает оранжевое солнце. Колонны облицованы белым мрамором, пол выложен серым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nВ 2010 году при наклеивании на путевую стену красной полосы с многократно повторяющимся названием станции, над декоративной отделкой станции был совершён акт вандализма: ряд уникальной плитки, более не производящейся, был варварски сбит перфораторами для освобождения места для красной полосы; при этом пострадало или отвалилось большое количество плитки в соседних рядах.\n\r\n    </p>\r\n    <p>\r\n      \nС 1995 до 2004 года (после затопления перегона между Лесной и Площадью Мужества) станция являлась конечной, до Площади Мужества можно было добраться на автобусе № 80, который ходил сравнительно часто и проезд в котором был бесплатным. В 2004 году, после ликвидации «Размыва», оборотный съезд за Лесной был разобран, а автобусный маршрут № 80 был закрыт.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('ligovskiy-pr', 'Лиговский проспект', 3, 1991, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Лиговский проспект — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга на Лиговском проспекте у перекрёстка с Транспортным переулком.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённому проспекту (название не очень удачно, поскольку на Лиговском проспекте есть несколько станций других линий и планируется построить ещё целую новую линию, проходящую под проспектом на всём его протяжении).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Ю. В. Еечко, Н. В. Ромашкин-Тиманов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПутевые стены и пол отделаны красным гранитом с полосами из светлого мрамора (на стенах) и серого гранита (на полу).\n\r\n    </p>\r\n    <p>\r\n      \nПод сводом станции над путями подвешены массивные декоративные люстры с четырьмя плафонами каждая. На этой станции впервые в истории Петербургского метрополитена применено оранжевое натриевое освещение, это единственная станция, где оно было установлено изначально. Однако во время ремонта в 2014 году оранжевое освещение было заменено на белое.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции были установлены оригинальные декоративные указатели, выполненные в едином стиле со светильниками и арками из металлических стержней, украшавших свод станции. Обе эти детали оформления были уничтожены в 2010 году (указатели — полностью, арки — частично) при приведении станций к единообразному виду для внедрения «нового информационного пространства».\n\r\n    </p>\r\n    <p>\r\n      \nНаклонный ход этой станции также обладает отличительной особенностью: светильники в нём расположены не на балюстраде эскалаторов, а на стенах наклонного хода (впрочем, здесь это связано не с художественными причинами, а с экономией).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('lomonosovskaya', 'Ломоносовская', 7, 1970, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Ломоносовская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юго-востоке города на пересечении улиц Бабушкина и Полярников, недалеко от Ивановской улицы, являющейся частью Центральной Дуговой магистрали.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в честь знаменитого русского учёного М. В. Ломоносова, имя которого носил также Ленинградский (Императорский) фарфоровый завод, расположенный недалеко от станции.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. Я. Мачерет, Л. С. Чупина.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЛомоносовская завершает серию из шести “горизонтальных лифтов”, идущих подряд (от станции Василеостровская).\n\r\n    </p>\r\n    <p>\r\n      \nСтены станции облицованы белым мрамором и немного наклонены, пол выложен серым гранитом. Торцевую стену станции украшает бронзовый барельеф с портретом М. В. Ломоносова. К открытию станции он не был готов, поэтому изначально вместо него был установлен гипсовый макет.\n\r\n    </p>\r\n    <p>\r\n      \nНад каждым дверным проёмом платформенных дверей установлена латунная панель с названием станции. Более того, при наклеивании на карниз станции зелёной полосы «нового информационного пространства» с названиями станций по-русски и латиницей, над каждой дверью появилось ещё по два названия. Таким образом, Ломоносовская стала рекордсменом петербургского метро по количеству повторений названия станции на платформе — аж 144 раза!\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('mayakovskaya', 'Маяковская', 4, 1967, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Маяковская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в самом центре Петербурга под Невским проспектом у пересечения с улицей Марата.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в честь знаменитого поэта, именем которого названа также и улица, начинающаяся недалеко от станции.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Ю. В. Билинский, А. Д. Бочаров, Г. А. Михайлов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nМаяковская — пожалуй, самая ярко-красочная станция Петербургского метрополитена. Стены станции, установленные уступами, облицованы смальтой ярко-красного цвета, что должно выглядеть как развевающиеся красные флаги. В концах зала стены украшены мозаичными портретами Маяковского. Пол выложен серым гранитом.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('mezhdunarodnaya', 'Международная', 12, 2012, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Международная — колонно-стеновая трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-восточной части города во Фрунзенcком районе на перекрёстке Бухарестской улицы с улицей Белы Куна.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в связи с расположением станции в районе, названия многих улиц которого посвящены городам и общественным деятелям дружественных стран Восточной Европы и Финляндии.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Н. В. Ромашкин-Тиманов, Я. В. Романова, Н. А. Виноградова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nХудожественное оформление станции должно было быть посвящено дружбе между народами и различным странам восточной Европы, однако впоследствии оно было упрощено, и в настоящее время станцию украшают мозаичные панно с Атлантом, держащим Землю (в торце центрального зала станции) и с Икаром, летящим рядом с Вавилонской башней (в наземном вестибюле станции над эскалаторами).\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены облицованы белым и серым мрамором, колонны облицованы металлом латунного цвета, полы выложены серым, красным и чёрным гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nПо традиции последних лет, вход встроен в большой торговый комплекс коробкообразного вида, и прямо из вестибюля станции можно не только выйти на улицу, но и войти в магазины.\n\r\n    </p>\r\n    <p>\r\n      \nМеждународная — самый долгий из долгостроев Ленинградского-Петербургского метрополитена: строительство было начато в конце 1980-х и затянулось более чем на 20 лет.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('moskovskaya', 'Московская', 16, 1969, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Московская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юге Санкт-Петербурга под Московской площадью, где пересекаются Московский и Ленинский проспекты и улица Типанова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади, а также в связи с тем, что недалеко от станции начинается Московское шоссе — главная дорога страны, соединяющая две столицы.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Н. В. Каменский, С. Г. Майофис.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nМосковская — cамая длинная станция типа “горизонтальный лифт”. Длина станции была увеличена чтобы вывести два выхода на разные стороны Московской площади. Наземных вестибюлей у станции нет, вход и выход осуществляются через подземные переходы.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции минимально. Станционные стены облицованы серым мрамором и украшены металлическим карнизом, пол выложен монотонным серым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение трёх лет (с 1969 до 1972) станция была конечной. Оборотный съезд за станцией был сохранён и используется по сей день.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('moskovskiye-vorota', 'Московские ворота', 13, 1961, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Московские ворота — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в южной части города на Московском проспекте у площади Московские ворота.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по Московским Триумфальным воротам, находящимся рядом со станцией.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. А. Петров, К. М. Митрофанов, А. И. Горицкий.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПилоны отделаны красно-коричневым мрамором с вертикальными алюминиевыми рёбрами. Путевые стены облицованы белой кафельной плиткой с чёрными вертикальными полосами. Раньше в центральном зале был «наливной пол» из искусственного камня в виде квадратов белого цвета, разделённых чёрной сеткой. В 2012 году на станции был проведён ремонт с заменой покрытия пола на гранитное, без сохранения цветового оттенка; также был уложен гранит вместо асфальта в боковых залах станции.\n\r\n    </p>\r\n    <p>\r\n      \nВ торце центрального зала расположено скульптурное произведение, аналогичное установленным на Московских воротах.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('narvskaya', 'Нарвская', 15, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Нарвская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-западной части города в Кировском районе на площади Стачек.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в честь Нарвских триумфальных ворот, расположенных на площади рядом со станцией.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. В. Васильев, Д. С. Гольдгор, С. Б. Сперанский.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nНарвская — самая богато укращенная скульптурами станция Петербургского метрополитена: на пилонах установлено 48 скульптурных групп (12 сюжетов по 4 раза каждый). Тема художественного оформления — трудовая доблесть советского народа, скульптуры изображают тружеников различных профессий.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены и пилоны облицованы белым мрамором. Пол центрального зала выложен красным гранитом и обрамлён сложным геометрическим орнаментом из чёрного и белого камня. Посадочные платформы раньше были покрыты асфальтом, который недавно был заменён на серый гранит.\n\r\n    </p>\r\n    <p>\r\n      \nВ торце центрального зала станции было установлено панно с образом И. В. Сталина, но оно в 1961 году, после XXII съезда КПСС, было закрыто от пассажиров декоративной стеной, за которой потом соорудили служебные помещения.\n\r\n    </p>\r\n    <p>\r\n      \nВ наземном вестибюле станции, над входом на эскалаторы, находится скульптурная композиция «Слава Труду!», в которой отсутствует центральный персонаж. Если присмотреться, то можно заметить, что люди смотрят в центр композиции, а там никого нет. Там должен был быть И. В. Сталин.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1);
INSERT INTO `station` (`path`, `name`, `position`, `foundation_year`, `description`, `line_id`) VALUES
('nevskiy-prospekt', 'Невский проспект', 9, 1963, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Невский проспект — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в самом центре Санкт-Петербурга на Невском проспекте в районе Думской и Михайловской улиц и канала Грибоедова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: Московско-Петроградская линия в этом месте пересекает Невский проспект (название не очень удачно, поскольку вдоль Невского проспекта располагается аж семь станций всех пяти линий).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — С. Г. Майофис.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nНевский проспект — центральная петербургская станция, находящаяся в самом центре города. Однако художественное оформление станции не по-центральному минималистично и позорно для станции, расположенной под главным проспектом северной столицы. Пилоны просто оштукатурены, окрашены и отделаны алюминиевыми рёбрами. Путевые стены раньше были облицованы красной плиткой, которая в 2006 году была заменена на искусственный камень похожего цвета. Пол центрального зала выложен чёрным лабрадоритом, полы боковых залов раньше были покрыты асфальтом, который был заменён на серый гранит в 2007 году. На стене поперечной камеры перехода, прерывающей одну из путевых стен, висит копия приказа 1971 года о присвоении Ленинградскому Метрополитену имени Ленина ордена Ленина — это единственный элемент декоративного украшения станции.\n\r\n    </p>\r\n    <p>\r\n      \nНевский проспект — одна из самых напряженных станций Петербургского метрополитена. Пересадочный узел Невский проспект — Гостиный двор состоит из двух переходов и трех выходов в город. Главный выход со станции ведёт на Михайловскую улицу. Поскольку выход ведет в историческую часть города, где было невозможно сооружение отдельно стоящего наземного вестибюля, вход и выход осуществляются через подземный переход. Второй выход со станции (через переход и станцию Гостиный двор) ведёт на канал Грибоедова, он встроен в здание.\n\r\n    </p>\r\n    <p>\r\n      \nИз бывшего оборотного тупика, находящегося за станцией, начинается служебная соединительная ветвь, которая ведет к станции Садовая Фрунзенско-Приморской линии. В некоторые годы поезда по этой соединительной ветви часто следуют с пассажирами, и проехать по такому необычному маршруту может любой желающий.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('novocherkasskaya', 'Новочеркасская', 5, 1985, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Новочеркасская — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к востоку от центра города в Красногвардейском районе под Заневской площадью на пересечении Новочеркасского и Заневского проспектов.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённому проспекту.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Г. Хильченко, В. Г. Чехман, А. С. Гецкин.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция изначально, после своего открытия в 1985 году, называлась Красногвардейская по названию района и проспекта, где она расположена. Переименована в Новочеркасскую при возвращении исторического названия проспекту. Об изменении названия существует городская легенда, что на путевых стенах новое название «НОВОЧЕРКАССКАЯ» составлено из старых букв «КРАСНОГВАРДЕЙСКАЯ», расположенных в другом порядке, но в старом названии не было буквы «Ч». При изготовлении новой буквы о её облике не подумали, и новая буква получилась совсем в другом стиле и очень выделялась на общем фоне, что было всем видно в течение 19 лет. В 2011 году были изготовлены новые буквы «Ч», совпадающие по стилю с остальными буквами.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены станции облицованы розовым гранитом. Пол выложен серым гранитом с красными вставками. Под сводом находятся огромного размера семь люстр кольцевой формы. Изначально они были украшены массивными медными знамёнами (которые должны были подчёркивать тему художественного оформления станции, борьбу Красной Гвардии за Советскую власть), однако из-за опасности обрушения они были демонтированы. При открытии станции она была освещена условно-белым светом, однако спустя небольшое время освещение было заменено на оранжевое натриевое.\n\r\n    </p>\r\n    <p>\r\n      \nВыход осуществляется через подземный переход, из которого на разные стороны Заневской площади ведут 12 выходов — это самое большое количество выходов из подземного перехода в Петербурге.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('obuhovo', 'Обухово', 9, 1981, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Обухово — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юго-восточной окраине города в Невском районе на улице Грибакиных рядом с КАД и железнодорожной станцией Обухово.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию местности, близлежащей железнодорожной станции и Обуховскому заводу.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. В. Попов, И. Н. Кусков, Л. М. Четыркин, И. Г. Лоханов, М. В. Меккель.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nОбухово — единственная станция односводчатого типа на 3-й линии. Путевые стены облицованы белым мрамором, пол выложен монотонным серым гранитом. Торцевую стену украшает бронзовый горельеф, посвящённый революционной борьбе пролетариата, работающего на Обуховском заводе. Центральная фигура горельефа — нетипично острый профиль В. И. Ленина. Изначально в торце станции находился бюст В. И. Ленина, который был убран в 1994 году.\n\r\n    </p>\r\n    <p>\r\n      \nПо оси зала станции установлены светильники-торшеры из чеканной меди. Раньше станция была освещена условно-белым светом ртутных ламп, но в конце первого десятилетия 2000-х освещение было заменено на оранжевое натриевое.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('obvodny-kanal', 'Обводный канал', 9, 2010, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Обводный канал — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга во Фрунзенском районе на пересечении Лиговского проспекта с набережной Обводного канала.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в этом месте Фрунзенско-Приморская линия пересекает Обводный канал (название выбрано неудачно: Обводный канал имеет длину более 8 километров и на его набережных находится несколько станций метро разных линий, например, Балтийская и Фрунзенская).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Б. А. Подольский, П. В. Малмалаев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЗа несостоявшуюся реализацию исходного проекта обидно больше всего в истории Ленинградского-Петербургского метрополитена. Подземный зал станции должен был быть двухэтажным, красивым и удобным (примерно как Спортивная) для быстрой кросс-платформенной пересадки на будущую Красносельско-Калининскую линию, строительство которой должно было начаться спустя несколько лет после открытия этой станции. Трассировка Фрунзенского радиуса была специально изогнута таким образом, чтобы он вышел к месту станции Обводный канал параллельно с Красносельско-Калининской линией для организации кросс-платформенной пересадки (в результате такого искривления станция получила менее удобный выход в город: до эскалаторов нужно подняться по лестнице и пройти по изогнутому коридору). Однако в 2000-е годы проект был упрощён, в результате получилась типовая пилонная 3-сводчатая станция глубокого заложения уменьшенного размера (как Волковская и Спасская). Проект удобной кросс-платформаменной пересадки уже отменили, но трассировку частично построенных в 90-е годы тоннелей линии было уже не изменить, в результате чего мы получили ненужную загогулину на линии, неудобный выход со станции и неудобную пересадку. Зато сэкономили.\n\r\n    </p>\r\n    <p>\r\n      \nТем не менее, художественное оформление станции было сделано очень красивым и необычным на фоне других новых станций. Тема оформления — промышленный Петербург конца XIX века. Подземные залы станции и наземный вестибюль украшены металлоконструкциями в промышленном стиле, на путевых стенцах и в торцах станции располагаются стеклокерамические фотопанно с видами промышленного Петербурга XIX века (художники — А. Я. Гордин и П. Э. Якушин).\n\r\n    </p>\r\n    <p>\r\n      \nВестибюль встроен в комплекс нового здания 8-этажного торгово-развлекательного комплекса с башенкой, как говорят, с сохранением градостроительной идеи 1911 года: здание проектировалось почти точной копией здания на противоположной стороне Лиговоского проспекта, но получилось как всегда.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('ozerki', 'Озерки', 3, 1988, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Озерки — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северной окраине города в Выборгском районе на Поклонной горе между проспектом Энгельса и Выборгским шоссе недалеко от их слияния.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию района Озерки, получившему своё имя от Суздальских озёр, одно из которых находится рядом со станцией.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Ф. Дроздов, Г. Н. Булдаков, В. В. Попов, Л. М. Четыркин, И. Г. Лоханов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nОзерки — станция с органичным художественным оформлением на тему природы, что обусловлено названием и местом расположения станции.\n\r\n    </p>\r\n    <p>\r\n      \nПол выложен серым гранитом, по оси станции на полу размещены четыре мозаики, изображающие различные деревья, характерные для наших краёв. Стены станции облицованы травертином. Торец центрального зала украшает большое мозаичное панно с озером, деревьями и птичками. Ещё одна мозаика «Времена года» помещена на стену со стороны эскалаторов. Авторы художественного оформления — С. Н. Репин, Н. П. Фомин, В. В. Сухов, И. Г. Уралов.\n\r\n    </p>\r\n    <p>\r\n      \nНеобычно выглядят светильники: они выполнены в виде сложных органических молекулярных соединений (тем не менее, более простых по структуре, нежели на Менделеевской в Москве); а может быть это просто икринки в озере, ведь это же Озерки, а не Химическая.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('park-pobedy', 'Парк Победы', 15, 1961, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Парк Победы — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юге Санкт-Петербурга в Московском районе около пересечения Московского проспекта с Бассейной улицей.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: станция находится рядом с Московским парком Победы.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. К. Андреев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПарк Победы — последняя станция первой очереди второй линии Ленинградского метрополитена. Это первая в мире станция типа “горизонтальный лифт”.\n\r\n    </p>\r\n    <p>\r\n      \nСтанционные стены облицованы белым мрамором и отделаны закруглёнными панелями из гофрированого алюминия. Светильники установлены на карнизе и скрыты за полосами из узорчатого стекла. Торцевая стена украшена декоративной арочной решёткой параболической формы с названием станции; раньше на ней был размещён лозунг «Да здравствует единый советский народ, строитель коммунизма».\n\r\n    </p>\r\n    <p>\r\n      \nНа станции были наливные бетонные полы в виде тёмно-красных треугольников на голубом фоне, что по замыслу архитектора должно символизировать победоносные знамёна на фоне голубого неба. В начале 2010-х годов на станции был проведён ремонт, сопровождавшийся актом вандализма: оригинальный пол был уничтожен, вместо него был уложен монохромный коричневый гранит без сохранения исходного рисунка и цвета. Проект замены вызвал протест со стороны ещё живого архитектора, однако его мнение было проигнорировано.\n\r\n    </p>\r\n    <p>\r\n      \nС момента своего открытия в течение восьми лет до 1969 года станция была конечной. После продления линии съезд между путями был разобран, но остался участок линии, где оба пути проходят в одном тоннеле, и, если повезёт, то через окно вагона можно увидеть проходящий встречный поезд.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции, построенный по примитивному типовому проекту, находится около входа в Московский парк Победы, один из двух парков Победы в Санкт-Петербурге\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('parnas', 'Парнас', 1, 2006, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Парнас — наземная станция, крытая, с боковыми платформами.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северной окраине Петербурга на улице Михаила Дудина (бывшее продолжение 3-го Верхнего переулка) к югу от нового жилого района «Северная долина».\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: от промзоны Парнас, начинающейся в 500 метрах к востоку от станции, которая получила своё название от исторического названия горы Парнас в Шуваловском парке, располагающемся в двух километрах западнее.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Н. В. Ромашкин-Тиманов, В. Г. Хильченко, М. В. Павлова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nВ год открытия станция была расположена буквально “в чистом поле” — к северу от городской застройки Санкт-Петербурга (отделена от неё железной дорогой) на продолжении 3-го Верхнего переулка, ведущего от промзоны Парнас. Вокруг станции было очень просторно и не было никаких объектов притяжения пассажиропотоков. Официальной причиной строительства станции в этом месте называлась необходимость защитить порталы тоннелей перед въездом в депо от неблагоприятных погодных условий. Но за несколько лет к северу от станции был построен огромный жилой квартал с высотными зданиями и тесными улицами, пассажиропоток существенно увеличился.\n\r\n    </p>\r\n    <p>\r\n      \nДействующие выходы и входы станции ориентированы на север, поэтому приехав на станцию, можно выйти прямо через двери, а чтобы зайти на станцию с улицы, нужно подняться по лестнице вверх, перейти над путями и снова спуститься вниз.\n\r\n    </p>\r\n    <p>\r\n      \nОтделка станции выполнена в светлых тёплых тонах, стены облицованы белым мрамором «коелга», пол выложен светло-серым гранитом с чёрными полосами, а потолок изготовлен из композитного материала «Алюмобонд». Станцию украшают художественные витражи древнегреческой тематики (авторы — А. К. Быстров, Е. Быстров, Г. Гуласов, С. Хвалов).\n\r\n    </p>\r\n    <p>\r\n      \nЗа станцией находится самое новое электродепо ТЧ-6 «Выборгское», обслуживающее Московско-Петроградскую линию. Ранее оно обслуживало Правобережную (до 2009) и Фрунзенско-Приморскую (до 2013) линии.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('petrogradskaya', 'Петроградская', 7, 1963, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Петроградская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Петроградского района на Каменноостровском проспекте рядом с площадью Льва Толстого и Большим проспектом Петроградской стороны.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по названию района (название не очень удачно, поскольку в районе расположено 4 станции метро, и их число в будущем увеличится).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Ф. Белов, А. В. Говорковский, В. Л. Ривин, Н. Н. Трегубов, Л. И. Шимаковский.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция проектировалась и строилась в хрущёвский период борьбы с архитектурными излишествами и почти не имеет художественного оформления. В торце на синей решётке, отгораживающей служебные помещения, размещено панно с профилями рабочего и крестьянки, символизирующими рабочий Петроград. Стены станции были выложены белой рельефной керамической плиткой нетипичной формы (в 2013 году был выполнен ремонт с полной заменой облицовки, но формы и размеры элементов были сохранены). Пол выложен серым гранитом с белыми полосами по краям.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции встроен в «Дом Мод». Петроградская — одна из двух петербургских станций, из наземных вестибюлей которых начинаются подземные пешеходные переходы.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение 19 лет (с 1963 до 1982) Петроградская была конечной станцией Московско-Петроградской линии.\n\r\n    </p>\r\n    <p>\r\n      \nВ будущем на Петроградской планируется переход на проектируемую кольцевую линию, которая пройдет через Петроградскую на участке от Спортивной до Лесной или Выборгской.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('pionerskaya', 'Пионерская', 5, 1982, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Пионерская — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северо-западе города в Приморском районе на перекрёстке проспекта Испытателей с Коломяжским проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: станция была открыта в год 60-летнего юбилея Всесоюзной пионерской организации.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, В. Г. Чехман.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nДо открытия станции Комендантский проспект на Приморском радиусе весной 2005 года Пионерская была одной из самых напряженных станций второй линии, а в течение «размыва» между Лесной и Площадью Мужества c 1995 до 2004 она была самой напряженной станцией во всём метро, в связи с чем работала в особом режиме: по утрам станция была открыта только на вход, а по вечерам все три эскалатора работали на выход, вход на станцию был закрыт. Но в настоящее время пассажиропоток на Пионерской сравним с другими станциями.\n\r\n    </p>\r\n    <p>\r\n      \nМинималистичное художественное оформление станции посвящено Пионерскому движению. Белые путевые стены с красным фризом по всей длине не могут напоминать ничто иное, кроме как красный пионерский галстук на белой рубашке. Больше никаких украшений в зале станции не наблюдается, даже глухой торец пуст. Пол выложен монотонным серым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nВ конце первого десятилетия 2000-х на станции был выполнен косметический ремонт, в результате которого её облик немного изменился: мелкая рельефная керамическая плитка, которой были облицованы путевые стены, была заменена на плоские керамогранитные плиты. При этом были утрачены большие металлические буквы, которыми было выложено название станции на стенах.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('pl-aleksandra-nevskogo', 'Площадь Александра Невского', 5, 1967, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Площадь Александра Невского — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на восточной окраине центрального района Санкт-Петербурга под площадью Александра Невского.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади, на которой расположен выход со станции; напротив находится Александро-Невская Лавра.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Л. П. Лавров, Т. В. Шишкова, В. Г. Шишков.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтены станции облицованы белым мрамором, пол выложен монотонным серым гранитом. На фоне остальных станций подобного типа стены Площади Александра Невского выделяются тем, что они сильно наклонены, что создаёт необычную перспективу при взгляде под углом. Над дверными проемами были установлены световые панели, изначально имевшие необычный ребристый рельеф, но впоследствии заменённые на плоское матовое стекло. К сожалению, во время ремонта станции в первом десятилетии 2000-х эти световые панели были демонтированы и заменены на типовые офисные светильники.\n\r\n    </p>\r\n    <p>\r\n      \nТорец станции раньше был украшен кованой решёткой с бронзовым барельефом с изображением войска Александра Невского. При строительстве перехода на Правобережную линию это украшение было перенесено в поперечную камеру перехода. В народе барельеф известен как «пять мужиков на четырёх конях»: если присмотреться, то можно заметить, что на переднем плане композиции находятся пятеро всадников, но коней у них только четыре.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль встроен в здание гостиницы Москва.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('pl-aleksandra-nevskogo2', 'Площадь Александра Невского 2', 4, 1985, '', 4),
('pl-lenina', 'Площадь Ленина', 8, 1958, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Площадь Ленина — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к северу от центра города в Калининском районе около площади Ленина, улицы Комсомола и Боткинской улицы.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. К. Андреев, Ю. Н. Козлов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПутевые стены отделаны белой керамической плиткой со смещением рядов. Пилоны в центре станции облицованы коричневым гранитом, крайние — коричневым мрамором с белыми прожилками. Стены боковых залов со стороны платформ облицованы белым мрамором. Пол выложен красным гранитом с белыми полосами по краям, в проходах между пилонами — серым.\n\r\n    </p>\r\n    <p>\r\n      \nПри открытии станции центральный зал освещался люстрами, позднее они были демонтированы. В 2006 году на станции была проведена ещё одна замена освещения, вместо ламп условно-белого цвета были установлены оранжевые натриевые лампы.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции два выхода, что нетипично для большинства петербургских станций. Южный выход считается основным, он ведёт к Финляндскому вокзалу, улице Комсомола и, собственно, к площади Ленина. Второй выход был открыт на четыре года позже, он ведёт на Боткинскую улицу и к улице академика Лебедева.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('pl-muzhestva', 'Площадь Мужества', 5, 1975, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Площадь Мужества — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на севере города в Калининском районе на Политехнической улице рядом с площадью Мужества.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Л. Л. Шретер, Е. М. Рапопорт, А. Я. Свирский, Л. И. Шимаковский, П. И. Юшканцев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПлощадь Мужества — одна из двух первых станций односводчатого типа в Ленинграде (вторая — Политехническая). Экспериментальный проект был признан удачным и в последующие 24 года в Ленинградском-Петербургском метрополитене появилось полтора десятка станций такой конструкции на всех четырёх (теперь уже — пяти) линиях.\n\r\n    </p>\r\n    <p>\r\n      \nПри строительстве тоннеля между Лесной и Площадью Мужества строители наткнулись на плывун — смесь воды и песка, заполнявшую древнее подземное русло Невы. Плывун был заморожен и тоннели были пройдены, но через 20 лет (в 1995 году) замороженный грунт оттаял и тоннели были затоплены чтобы предотвратить прорыв плывуна в тоннель и проседание грунта на поверхности. В течение почти девяти лет велись работы по проектированию и строительству новых тоннелей, движение поездов было восстановлено летом 2004 года.\n\r\n    </p>\r\n    <p>\r\n      \nВ связи с открытием станции к 30-летию победы в Великой Отечественной войне и её близкому расположению к площади Мужества и Пискарёвскому мемориальному кладбищу, тематика оформления станции посвящена героизму ленинградцев в годы войны. Светильники станции, расположенные над путями на массивных консолях, стилизованы под чаши Вечного огня. Северный торец станции украшает пятиконечная звезда, а в южном торце над проходом к эскалаторам размещена надпись «СЛАВА ГЕРОЯМ ТЕБЯ ЛЕНИНГРАД ОТСТОЯВШИМ». Путевые стены облицованы серым мрамором, пол выложен серым гранитом. В 2010 году был проведён ремонт облицовки путевых стен, их вид был немного упрощён: раньше в облицовке чередовались углублённые и выступающие участки, теперь же стены абсолютно плоские.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции был построен в виде отдельно стоящего здания, к которому примерно через 20 лет был пристроен жилой дом. При взгляде с Политехнической улицы кажется, что вестибюль встроен в этот дом, но на самом деле здание вестибюля находится в его дворе.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('pl-vosstaniya', 'Площадь Восстания', 10, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Площадь Восстания — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга у пересечения Лиговского и Невского проспектов и улицы Восстания на площади Восстания.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. В. Ганкевич, Б. Н. Журавлёв, И. И. Фомин.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПлощадь Восстания — конечная станция первого участка первой линии Ленинградского метрополитена.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции посвящено Октябрьскому вооружённому восстанию 1917 года. Пилоны станции украшены шестью литыми медальонами с барельефами с изображениями революционных событий, два из которых повторяются. С обеих сторон станции, слева от входа, на втором пилоне находятся барельефы, на которых остались единственные уцелевшие и доступные для всеобщего обозрения изображения Сталина в Санкт-Петербурге.\n\r\n    </p>\r\n    <p>\r\n      \nЦоколь пилонов и путевых стен облицован красным уральским мрамором, высота цоколя — около метра, это минимум среди всех станций метро в Петербурге. Своды центрального зала богато украшены лепниной и увенчаны светильниками, образующими световые арки. Боковые залы освещены люстрами, похожими на люстры на станции Новослободской Кольцевой линии в Москве.\n\r\n    </p>\r\n    <p>\r\n      \nПол центрального зала станции выложен красным гранитом с чёрными полосами по бокам, их разделяет зигзагообразная белая полоса в виде квадратной волны. Полы боковых залов раньше были покрыты асфальтом, но в 1993 году поочерёдно был проведён ремонт боковых залов, в результате которого платформа первого пути (в сторону Чернышевской) оказалась выложена серым кранитом, а со стороны второго пути (в сторону Владимирской) были уложены совершенно другие гранитные плиты — красные, а вперемежку с ними серые в белую крапинку.\n\r\n    </p>\r\n    <p>\r\n      \nПлощадь Восстания — первая в Петербурге-Ленинграде станция с двумя выходами. Наземный вестибюль станции, расположенный на площади Восстания на пересечении Невского и Лиговского проспектов, является символом Петербугского метрополитена. Второй наземный вестибюль встроен в здание Московского вокзала, он был открыт в 1960 году.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('politehnicheskaya', 'Политехническая', 4, 1975, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Политехническая — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на севере города в Калининском районе на Политехнической улице недалеко от перекрёстка с улицей Гидротехников.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: наземный вестибюль станции находится рядом с Санкт-Петербургским Государственным Политехническим университетом (ранее — Ленинградский Политехнический институт).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — С. Б. Сперанский, Н. В. Каменский, Л. Г. Бадалян.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПолитехническая — одна из двух первых станций односводчатого типа в Ленинграде (вторая — Площадь Мужества). Экспериментальный проект был признан удачным и в последующие 24 года в Ленинградском-Петербургском метрополитене появилось полтора десятка станций такой конструкции на всех четырёх (теперь уже — пяти) линиях.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции минималистично и не посвящено ни историческим событиям, ни местности, где располагается станция. Изначально путевые и торцевые стены были облицованы травертином — пористым известняком серого цвета с лёгким зеленовато-желтоватым оттенком, слегка отполированного. Над травертиновой облицовкой во всю длину путевых стен была установлена полоса из анодированного алюминия розового цвета, таким же образом были оформлены технологические двери. Освещение станции было установлено на консолях, выступающих со стыка путевых стен и свода; свет ртутных ламп зеленовато-голубого цвета хорошо сочетался с цветом облицовки. Также имелась дополнительная световая полоса под карнизом, подсвечивающая название станции на путевых стенах.\n\r\n    </p>\r\n    <p>\r\n      \nОднако даже такое минималистическое оформление было испорчено аж три раза в ходе трёх последовательных “ремонтов”. Сначала, перед открытием “размыва” (в первой половине 2004 года) голубые ртутные лампы были заменены на оранжевые натриевые. Зеленоватый травертин и розовый алюминий сразу перестали “играть” оттенками в таком свете, и всё стало выглядеть равномерно серо-рыжим: что пол, что стены, что белый свод. Одновременно с этим была демонтирована световая полоса, подсвечивавшая названия станции на путевой стене. Затем, в 2005 году, в ходе ремонта торцевых стен, их облицовка была заменена с каменной на композитные панели, похожие на крашеный гипсокартон. А в ходе ремонта 2010 года была заменена облицовка путевых стен: полосы из анодированного алюминия розового цвета были демонтированы, вместо них установили крашеные в жёлтый цвет панели из материала типа сайдинга или профнастила, а исходный травертин был заменён на новый камень, немного похожий, но чисто светло-серый (без исходного желтовато-зеленоватого отлива) и не такой блестящий, как старый. Для полноты “модернизации” остаётся ещё заменить гранитное покрытие пола на асфальтовое, а потолок закрыть пластиковыми панелями (к счастью, это ещё не произошло).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('pr-bolshevikov', 'Проспект Большевиков', 7, 1985, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Проспект Большевиков — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в юго-восточной части города в Невском районе на улице Коллонтай около перекрёстка с проспектами Пятилеток, Большевиков и Российским.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в связи с расположением недалеко от начала проспекта Большевиков, крупной городской магистрали (название выбрано неудачно: из всех улиц и проспектов перекрёстка проспект Большевиков не является ближайшим к станции, но при этом непосредственно на проспекте Большевиков есть другая станция этой линии — Улица Дыбенко)..\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Ю. В. Еечко, Р. Ш. Розенталь.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПутевые стены облицованы серо-коричневым гранитом. Пол выложен серым гранитом. Cвод станции несколько необычен по сравнению с другими односводчатыми станциями: на своде имеется декоративный выступ. На торцевой стене подвешен внушительных размеров серп и молот — самый популярный декоративный элемент оформления советских станций метро.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение двух лет это была конечная станция Правобережной линии. В настоящее время оборотный съезд разобран.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт 4-й линии.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('pr-prosvescheniya', 'Проспект Просвещения', 2, 1988, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Проспект Просвещения — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на северной окраине города в Выборгском районе у перекрёстка проспекта Энгельса с проспектом Просвещения.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в этом месте Московско-Петроградская линия пересекает проспект Просвещения (название не очень удачно, поскольку проспект Просвещения имеет длину почти 8 километров, и его точно так же пересекает и Кировско-Выборгская линия на станции Гражданский проспект).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Ю. В. Еечко, В. Г. Чехман, В. Г. Хильченко.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЭто единственная станция колонного типа на Московско-Петроградской линии.\n\r\n    </p>\r\n    <p>\r\n      \nКолонны и путевые стены облицованы белым мрамором. Пол центрального зала выложен серым гранитом с тёмно-серыми поперечными полосами, пол платформ — тёмно-серым гранитом и пёстрым мрамором. Раньше в центральном зале материалы пола были такими же, как на платформе, но несколько лет назад мрамор был заменён на гранит.\n\r\n    </p>\r\n    <p>\r\n      \nРаньше станция была освещена условно-белым светом, в 2006 году освещение было заменено на оранжевое натриевое.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение 18 лет (с 1988 до 2006) станция была конечной.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('pr-veteranov', 'Проспект Ветеранов', 19, 1977, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Проспект Ветеранов — колонная трёхпролётная станция мелкого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на южной окраине города в Кировском районе на бульваре Новаторов у перекрёстка с Дачным проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией проходит проспект Ветеранов, важная магистраль района.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Г. Хильченко.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПроспект Ветеранов — одна из двух станций “московского типа” построенная по типовому проекту из сборных железобетонных конструкций, так называемая “новая сороконожка” с 26 парами колонн. В Москве таких станций — десятки, в Петербурге же всего две.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции строго и минималистично. Квадратные колонны облицованы чёрным лабрадоритом с латунными вставками, путевые стены отделаны розовато-золотистым мрамором «газган». Пол выложен серым гранитом с латунными вставками, а также строгими прямоугольиками из чёрного лабрадорита в центре станции.\n\r\n    </p>\r\n    <p>\r\n      \nНаземного вестибюля у станции нет, вход осуществляется через подземные переходы на бульваре Новаторов.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('primorskaya', 'Приморская', 1, 1979, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Приморская — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на западной окраине города в Василеостровском районе на острове Декабристов у пересечения улицы Одоевского с Наличной улицей.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: из-за расположения недалеко от Балтийского моря (название не очень удачно, потому что Приморский район города, бывший Ждановский, находится совсем в другом месте, а Невско-Василеостровская линия даже не имеет пересадки на Фрунзенско-Приморскую).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Н. Соколов, Н. И. Стародубов, В. А. Реппо.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПутевые стены и колонны облицованы серым рускеальским мрамором с прожилками разных оттенков и зеленоватым отливом. На стеновых вставках между коннами установлены медные медальоны с горельефами известных кораблей русского и советского флота. В глухом торце центрального зала станции располагается инсталляция настоящего якоря. На полу станции выложена светлым и темным гранитом роза ветров.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции встроен в здание дома связи метрополитена.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('proletarskaya', 'Пролетарская', 8, 1981, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Пролетарская — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юго-восточной окраине Петербурга в Невском районе на проспекте Обуховской обороны около пересечения с улицей Чернова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: станция находится в районе массового проживания пролетариата, работавшего на Обуховском заводе.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Д. С. Гольдгор, Ю. В. Еечко, Р. Ш. Розенталь, Н. А. Афошин.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПролетарская — одна из самых глубоких станций, и одна из двух станций с каскадом из двух эскалаторов (вторая — Адмиралтейская).\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены и колонны облицованы светлым мрамором тёплых тонов, карниз над колоннами отделан розовым гранитом. Пол выложен серым и красным гранитом с чёрными поперечными полосами. Торец центрального зала станции украшает инсталляция «cерп и молот» — символ пролетариата и трудового крестьянства.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт третьей линии.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('pushkinskaya', 'Пушкинская', 12, 1956, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Пушкинская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на Загородном проспекте рядом с Витебским железнодорожным вокзалом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: Витебский вокзал, располагающийся рядом со станцией метро — начало железной дороги в Царское село (ныне — г. Пушкин), где находился лицей, в котором учился А. С. Пушкин.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Л. М. Поляков, В. А. Петров.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПушкинская считается самой красивой станцией Петербургского метрополитена. Художественное оформление полностью посвящено А. С. Пушкину. В торце центрального зала станции находится памятник Пушкину. Он стал началом традиции устанавливать памятники и мемориалы в метрополитенах СССР. Это не единственный памятник Пушкину в Петербургском метрополитене, второй находится на станции Чёрная речка Московско-Петроградской линии.\n\r\n    </p>\r\n    <p>\r\n      \nПилоны станции облицованы белым мрамором. В нишах пилонов установлены напольные светильники-торшеры с хрустальными чашами и многочисленными золотистыми металлическими украшениями. Путевые стены облицованы белой керамической плиткой. Освещены боковые залы массивными люстрами.\n\r\n    </p>\r\n    <p>\r\n      \nПол центрального зала станции выложен красным гранитом и был украшен сложным геометрическим орнаментом из белого и чёрного мрамора с обрамлением метлахской плиткой. В ходе ремонта в 2010-е годы все напольные материалы были заменены на гранит различных оттенков, близких к исходным. Также на гранит красного цвета было заменено асфальтовое покрытие в боковых залах.\n\r\n    </p>\r\n    <p>\r\n      \nСтанция была открыта на полгода позже остальных станций первой очереди из-за проблем со строительством наклонного хода.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('rybatskoye', 'Рыбацкое', 10, 1984, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Рыбацкое — наземная станция, крытая, с боковыми платформами.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юго-восточной окраине города в Невском районе за кольцевой автодорогой между Тепловозной улицей и 3-м Рыбацким проездом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию местности.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, Н. В. Ромашкин-Тиманов, К. Г. Леонтьев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанционные стены облицованы серый мрамором, колонны — белым. Полы выложены светлыми плитами из искусственного камня.\n\r\n    </p>\r\n    <p>\r\n      \nРядом со станцией находится одноименная железнодорожная станция. Станция метро и железнодорожная станция связаны между собой подземным переходом.\n\r\n    </p>\r\n    <p>\r\n      \nЗа станцией расположено электродепо ТЧ-5 «Невское», обслуживающее одновременно Невско-Василеостровскую и Правобережную линии.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('sadovaya', 'Садовая', 7, 1991, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Садовая — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга в Адмиралтейском районе на Сенной площади.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по Садовой улице, проходящей через Сенную площадь.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. И. Прибульский, Г. П. Кашихин, В. В. Попов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСадовая — единственная из односводчатых станций глубокого заложения в Петербурге с двумя торцевыми выходами из одного зала. Один выход — в город, другой — переход на вторую линию.\n\r\n    </p>\r\n    <p>\r\n      \nСадовая — старейшая станция Фрунзенско-Приморской линии, причём, единственная старейшая (следующие станции были открыты аж на 6 лет позже). Дело в том, что при строительстве к 1991 году готовность станции Площадь Мира III (будущая Садовая) оказалась несколько выше строящейся параллельно станции Площадь Мира II (будущей Спасской), и было принято решение временно подключить её к Правобережной линии. Это “временное” затянулось аж на 18 лет (до открытия Спасской в 2009 году), в течение которых все станции Приморского радиуса эксплуатировались в составе Правобережной линии.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение шести лет (с 1991 до 1997) Садовая была конечной станцией Правобережной линии.\n\r\n    </p>\r\n    <p>\r\n      \nВ настоящее время это единственная станция метро Петербурга, которая связана служебными соединительными ветками сразу с двумя линиями — с Правобережной (через Достоевскую) и с Московско-Петроградской (через Невский проспект).\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены и пол станции отделаны красным гранитом с небольшими серыми полосами. По исходному проекту путевые стены планировались серыми, но проект был изменён из-за перебоев поставок нужного камня.\n\r\n    </p>\r\n    <p>\r\n      \nОсвещение выполнено с помощью многочисленных дугообразных светильников, установленных под сводом станции. Изначально они были закрыты матовыми стёклами, которые через несколько лет были демонтированы из-за трудности обслуживания, а после этого была демонтирована часть освещения, в настоящее время лампы находятся только над краями платформы.\n\r\n    </p>\r\n    <p>\r\n      \nСтанция не имеет наземного вестибюля, вход осуществляется через подземный переход, четыре выхода из которого ведут на разные стороны Сенной площади и Садовой улицы.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('sennaya-pl', 'Сенная площадь', 10, 1963, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Сенная площадь — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга в Адмиралтейском районе на Сенной площади.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади, которая получила своё название по находившемуся на ней в XIX веке Сенному рынку.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. И. Прибульский, А. Я. Мачерет, В. В. Ганкевич.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nС момента своего открытия в 1963 году до 1991 года станция выглядела совершенно иначе: был очень короткий центральный зал, облицованный очень мелкой светлой кафельной плиткой, которую в народе прозвали “ириски”. Торец центрального зала украшала надпись «СССР — оплот мира».\n\r\n    </p>\r\n    <p>\r\n      \nВ 1991 году при строительстве перехода на станцию Садовая (Площадь Мира III) и заготовки под переход на станцию Спасская (Площадь Мира II), станция подверглась реконструкции и рестайлингу: центральный зал был существенно удлиннен, кафельная плитка была заменена на мрамор.\n\r\n    </p>\r\n    <p>\r\n      \nВ 2008 году, во время работ по сооружению перехода на Спасскую, внешний вид станции изменился ещё раз: на платформах асфальтовое покрытие было заменено на гранит и был обновлён пол в центральном зале. Торец центрального зала украсило новое мозаичное панно «Три века Санкт-Петербурга».\n\r\n    </p>\r\n    <p>\r\n      \nТеперь же о старом облике станции даже не напоминает облицовка путевых стен: в 2013 году голубая кафельная плитка на путевых стенах была заменена на искусственный керамогранит похожего цвета.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции расположен на месте снесённого храма «Спас-на-Сенной» В 1999 году здесь случилась трагедия: козырек наземного вестибюля обрушился, погибло несколько человек.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('spasskaya', 'Спасская', 1, 2009, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Спасская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре Санкт-Петербурга в Адмиралтейском районе на Сенной площади.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: в память о разрушенном в 1961 году соборе Спас-на-Сенной.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Е. М. Рапопорт, В. Н. Морозова, В. Г. Хозацкий.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПилоны и путевые стены облицованы тёмно-жёлтым гранитом. Пол выложен серым и коричневым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nОформление станции посвящено зодчеству Санкт-Петербурга. Станцию украшают два панно (художники — С. Н. Репин, В. В. Сухов, И. Г. Уралов, Н. П. Фомин), на одном из которых перечислены имена известных зодчих и архитекторов Санкт-Петербурга. Оно печально знаменито тем, что имена некоторых зодчих выгравированы с ошибками.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4);
INSERT INTO `station` (`path`, `name`, `position`, `foundation_year`, `description`, `line_id`) VALUES
('sportivnaya', 'Спортивная', 5, 1997, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Спортивная — односводчатая станция глубокого заложения, двухъярусная.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к северо-западу от центра города под Тучковым мостом, соединяющим Петроградский район с Васильевским островом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией находятся два спортивных комплекса: стадион «Петровский» и дворец спорта «Юбилейный», а открытие станции планировалось к Олимпийским играм (Россия не получила право на проведение Олимпиады в те годы, но название станции было уже утверждено).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Константинов, В. С. Волонсевич, О. А. Кузнецов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСпортивная — самая уникальная станция Петербургского метрополитена. Это единственная в бывшем СССР двухэтажная станция односводчатого типа для четырех путей одновременно. Поезда одной линии приходят на разные этажи. Второй путь на каждом этаже пока не задействован, в будущем это будет пересадочный узел с кольцевой линией.\n\r\n    </p>\r\n    <p>\r\n      \nВторая особенность Спортивной — западная часть станции находится под Малой Невой. Спустя 18 лет после открытия, станция получила второй выход на Васильевский остров.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены верхнего зала станции облицованы облицованы белым известняком, пол выложен светло-серым гранитом. В глухом торце размещён текст «Оды Спорту» Пьера Кубертена. Над путями верхнего зала установлены навесные светильники, имитирующие олимпийские факелы, а с нижнего яруса на верхний через отверстия проходят три торшера. Изначально станция была освещена условно-белым светом, но в период с 2012 до 2014 года лампы постепенно заменялись на оранжевые натриевые.\n\r\n    </p>\r\n    <p>\r\n      \nПутевые стены нижнего зала облицованы коричневым гранитом, пол выложен светло-серым гранитом с коричневыми полосами и кругами (при строительстве второго выхода один из кругов был уничтожен, а часть серого гранита на полу заменена на розовый). Путевые стены украшены мозаиками в античном стиле, символизирующие участников древних Олимпиад.\n\r\n    </p>\r\n    <p>\r\n      \nНаземных вестибюлей у Спортивной нет, выходы осуществляется через подземные переходы на пересечении Большого проспекта Петроградской стороны и проспекта Добролюбова, а также на пересечении набережной Макарова с 1-й и Кадетской линиями Васильевского острова.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('staraya-derevnia', 'Старая Деревня', 2, 1999, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Старая Деревня — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в северо-западной части города в Приморском районе на Торфяной дороге.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию местности.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — В. Н. Щербин, И. П. Макаюда.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПутевые стены облицованы серо-голубоватым мрамором «уфалей», отделка выполнена с протяжёнными, но небольшими по глубине арочными углублениями. Пол выложен серым и коричневым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nПо оси станции установлено семь высоких светильников, стилизованных под городские фонари, с восемью плафонами каждый.\n\r\n    </p>\r\n    <p>\r\n      \nВ течение 6 лет (с 1999 до 2005) Старая Деревня была конечной станцией.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт пятой линии. В тупике за станцией находится ПТО, в связи с чем несколько раз в день некоторые поезда следуют только до Старой Деревни.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('tehnologicheskiy-institut', 'Технологический институт', 13, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Технологический институт — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в Адмиралтейском районе Санкт-Петербурга на перекрёстке Московского и Загородного проспектов.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: рядом со станцией находится Санкт-Петербургский государственный технологический институт (ныне — технический университет).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. М. Соколов, А. К. Андреев (первый зал), А. И. Прибульский, А. Я. Мачерет, В. В. Ганкевич (второй зал).\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nТехнологический Институт — первая в СССР двухзальная станция с перекоммутацией путей и кроссплатформенной пересадкой, обслуживающая одновременно две линии. Поезда попутных направлений двух линий прибывают в один зал, для пересадки в попутном направлении достаточно перейти платформу. Для следования в обратном направлении нужно перейти в другой зал по короткому переходу.\n\r\n    </p>\r\n    <p>\r\n      \nПервый зал станции (колонного типа) был открыт в 1955 году и в течение первых шести лет обслуживал единственную линию. Его стены облицованы белым мрамором и украшены бронзовыми барельефами ученых.\n\r\n    </p>\r\n    <p>\r\n      \nВторой зал станции (пилонный) был открыт в 1961 году 29 апреля вместе с Московско-Петроградской линией. В оформлении он намного проще по сравнению с первым: кроме надписей об успехах советской науки и техники, расположенных на пилонах, он ничем не украшен.\n\r\n    </p>\r\n    <p>\r\n      \nДо 1980 года из второго зала прямого выхода на поверхность не было, а сейчас выходы из обоих залов станции выведены в одно здание, в котором также располагается Управление Петербургского метрополитена.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('tehnologicheskiy-institut2', 'Технологический институт 2', 11, 1955, '', 2),
('udelnaya', 'Удельная', 4, 1982, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Удельная — односводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на севере города в Выборгском районе на Удельном проспекте между Елецкой и Енотаевской улицами.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой железнодорожной станции, обслуживающей Выборгское направление, начинающееся на Финляндском вокзале.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, В. Г. Хильченко, В. Н. Выдрин.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nХудожественное оформление станции посвящено историческому событию: нелегальному бегству В. И. Ленина от Временного правительства в Финляндию, куда он отправился именно с Удельной. На торцевую стену станции помещён барельеф с профилем Ленина и сопроводительный текст: «В начале августа 1917 года В. И. Ленин, скрываясь от преследования Временного буржуазного правительства, нелегально уехал на паровозе № 293 с железнодорожной станции Удельная в Финляндию. В начале октября 1917 года возвратился в Петроград для руководства подготовкой вооружённого восстания.»\n\r\n    </p>\r\n    <p>\r\n      \nРаньше станция была освещена условно-белым светом, в 2000 году освещение было заменено на оранжевое натриевое.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт второй линии. В тупике за станцией находится ПТО, в связи с чем несколько раз в день некоторые поезда следуют только до Удельной.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2),
('ul-dybenko', 'Улица Дыбенко', 8, 1987, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Улица Дыбенко — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на юго-восточной окраине города в Невском районе на проспекте Большевиков около пересечения с улицей Дыбенко.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по пересекаемой улице.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — О. Г. Кравцов, М. П. Антонов, Г. А. Васильев.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nКолонны станции облицованы тёмно-серым гранитом, путевые стены выложены бело-жёлтым мрамором. Тема художественного оформления станции — революционные символы: станцию украшают 7 мозаичных панно, изображающих серп, молот, штык, знамя, винтовку, лозунги и прочие символы революции (авторы мозаик — художники И. Г. Уралов, С. Н. Репин, Н. П. Фомин, В. В. Сухов).\n\r\n    </p>\r\n    <p>\r\n      \nСтанция была открыта к 70-летию Великой Октябрьской Социалистической революции, в связи с чем сначала планировалось назвать её «Красных комиссаров», однако это название не было утверждено (возможно, по той причине, что русскому языку не свойственны топонимы в генетиве без существительного в именительном падеже). В 2007 году предлагалось переименовать станцию в «Весёлый посёлок» — по историческому названию района, однако это название тоже не было утверждено (возможно, во избежание путаницы с соседней станцией «Проспект Большевиков», которая также находится в Весёлом посёлке).\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 4),
('vasileostrovskaya', 'Василеостровская', 2, 1967, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Василеостровская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к западу от центра Петербурга на Васильевском острове у перекрёстка Среднего проспекта с 6-й и 7-й линиями..\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по названию острова и административного района (название не очень удачно, потому что в Василеостровском районе есть и другие станции метро, и их количество будет существенно увеличено).\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. Я. Мачерет, А. И. Прибульский, Л. С. Чупина.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nВасилеостровская начинает серию из шести “горизонтальных лифтов”, идущих подряд (до станции Ломоносовская).\n\r\n    </p>\r\n    <p>\r\n      \nСтены станции облицованы белым мрамором. Карниз стен украшен фризом из смальты цвета морской волны. Пол выложен серым гранитом с металлическими полосами.\n\r\n    </p>\r\n    <p>\r\n      \nВ торце установлена решётка с названием станции и годом её постройки. Станция укорочена: часть платформы отдана под служебные помещения, поэтому первые две двери в первом вагоне в сторону Приморской и последние две двери в последнем вагоне в сторону Гостиного двора закрыты. До 2001 года поезда проходили эту часть линии в сторону Гостиного двора с закрытым первым вагоном, и получалось, что для входа и выхода пассажиров были доступны двери только четырех с половиной вагонов.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции расположен на высоком цоколе из-за угрозы наводнений в этом районе.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('vladimirskaya', 'Владимирская', 11, 1955, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Владимирская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре города на перекрёстке Большой Московской улицы и Кузнечного переулка рядом с Владимирской площадью.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой площади.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. В. Жук, А. И. Прибульский, Г. И. Александров.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nПилоны и стены станции облицованы уральским мрамором «коелга». Пол центрального зала выложен чёрными и серыми гранитными плитами в шахматном порядке с обрамлением более светлыми полосами. Полы боковых залов выложены красновато-серым гранитом. Центральный зал станции освещён массивными люстрами, боковые залы — настенными светильниками.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции был очень короткий центральный зал, но в 1991 году, при строительстве перехода на Правобережную линию, станция была подвергнута реконструкции и центральный зал был существенно удлиннён.\n\r\n    </p>\r\n    <p>\r\n      \nВ 2004 году, при переводе линии на 8-вагонные составы, станция опять была подвергнута реконструкции: посадочные платформы станции были удлиннены за счет снесения служебных помещений, занимавших ранее часть боковых залов станции. В результате реконструкции торцы боковых залов выглядят совершенно непохоже на основную часть станции: основная часть станции облицована мрамором и освещена светильниками с желтоватым спектром, а торцы платформ просто выкрашены краской и освещены примитивными люминисцентными трубками.\n\r\n    </p>\r\n    <p>\r\n      \nИнтересный факт: у Владимирской был не только самый короткий центральный зал; ещё и перегон от Владимирской к Площади Восстания — самый короткий в Петербургском метрополитене, всего 720 метров.\n\r\n    </p>\r\n    <p>\r\n      \nНаземный вестибюль станции встроен в здание, в котором размещается проектный институт метрополитена «Ленметрогипротранс».\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('volkovskaya', 'Волковская', 10, 2008, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Волковская — пилонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к юго-востоку от центра города во Фрунзенском районе в начале Бухарестской улицы около перекрёстка с Волковским проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию местности Волкова деревня; рядом протекает река Волковка, на берегах которой лежит Волковское кладбище.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Константинов, Д. А. Бойцов.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтанция отделана современными материалами с целью удешевления строительства: металлопластиковыми панелями сиреневого цвета. Пол выложен чёрным гранитом с белыми полосами, формирующими квадраты и прямоугольники.\n\r\n    </p>\r\n    <p>\r\n      \nХудожественное оформление станции посвящено истории окружающей местности: станцию украшают два панно, изображающие Волкову деревню и реку Волковку, а наземный вестибюль станции украшен витражом с волками.\n\r\n    </p>\r\n    <p>\r\n      \nВолковская стала первой станцией, где тестировалось многократное повторение названия станции на цветных полосах на станционных стенах. Впоследствии аналогичные полосы были размещены на других станциях линии с демонтажом оригинальных названий, набранных декоративными металлическими буквами, а затем и на других линиях.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('vyborgskaya', 'Выборгская', 7, 1975, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Выборгская — колонная трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к северу от центра города в Выборгском районе у пересечения Лесного проспекта с улицей Смолячкова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по историческому названию района Выборгская сторона.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Гецкин, В. П. Шувалова, В. Г. Хильченко.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nВ качестве отделочного материала колонн и путевых стен использован травертин — мягкий известняк серовато-коричневатого цвета, что отличает эту станцию от многих других станций метро. Пол выложен серым гранитом.\n\r\n    </p>\r\n    <p>\r\n      \nНа станции расположен линейный пункт первой линии.\n\r\n    </p>\r\n    <p>\r\n      \nНа Выборгской со времён её открытия существует традиция, нетипичная для станций метро: в наземном вестибюле станции на площадке за эскалаторами растут живые цветы.\n\r\n    </p>\r\n    <p>\r\n      \nВыборгская — одна из двух станций Петербургского метрополитена, прямо из наземного вестибюля которых начинается подземный пешеходный переход. Переход проходит под железнодорожными путями Выборгского направления и является самым длинным в городе.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1),
('yelizarovskaya', 'Елизаровская', 6, 1970, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Елизаровская — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится к юго-востоку от центра Петербурга на пересечении улицы Бабушкина и проспекта Елизарова.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённому проспекту.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. К. Комалдинов, И. И. Комалдинова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nСтены станции облицованы белым мрамором и украшены медным карнизом с белым матовым стеклом, на котором раньше было установлено название станции. Пол выложен монотонным серым гранитом. Изначально на станции было только закарнизное освещение, но в 2006 году освещение было усилено, на карнизе были установлены типовые офисные светильники вместо матового стекла. Название станции на карнизе при этом было перенесено выше.\n\r\n    </p>\r\n    <p>\r\n      \nТорцевую стену украшает барельеф «Восстание пролетариата» на декоративной решетке, отделяющей служебные помещения от основного зала станции.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 3),
('zvenigorodskaya', 'Звенигородская', 8, 2008, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Звенигородская — колонно-стеновая трёхсводчатая станция глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится в центре города на Звенигородской улице рядом с перекрёстком с Загородным проспектом.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой улице.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — А. С. Константинов, Н. В. Ромашкин-Тиманов, В. Г. Хильченко, Ю. Ю. Подервянская, Т. Р. Баранова, М. В. Павлова.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЗвенигородская — самая необычная по цветовому оформлению среди станций, облицованных натуральным камнем. Стены облицованы белым мрамором «коелга», гранитом «Кашина гора» и тёмно-зелёным индийским мрамором Indiana Green. Пол выложен зеленоватым индийским гранитом Rakhi Green с цветными вставками и окантовкой из красного индийского гранита Imperial Red.\n\r\n    </p>\r\n    <p>\r\n      \nГлухой торец станции украшает мозаичное панно «Семёновский полк», казармы которого находились неподалёку в позапрошлом веке.\n\r\n    </p>\r\n    <p>\r\n      \nЗвенигородская — один из долгостроев Ленинградского-Петербургского метрополитена: строительство было начато в конце 1980-х и затянулось почти на 20 лет.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 5),
('zviozdnaya', 'Звёздная', 17, 1972, '<div id=\"station_info\">\r\n  <p id=\"station_name_type\">\r\n    Звёздная — станция закрытого типа глубокого заложения.\r\n  </p>\r\n  <p id=\"station_addr\">\r\n    Станция находится на южной окраине города в Московском районе у перекрёстка Звёздной улицы с улицей Ленсовета.\r\n  </p>\r\n  <p id=\"station_name_origin\">\r\n    Происхождение названия: по одноимённой улице.\r\n  </p>\r\n  <p id=\"station_creators\">\r\n    Архитекторы станции — Ю. В. Билинский, Г. А. Михайлов, Г. А. Шихалева.\r\n  </p>\r\n  <div id=\"station_descr\">\r\n    <p>\r\n      \nЗвёздная — самая неглубокая станция из станций глубокого заложения. Это последняя станция типа “горизонтальный лифт”. После 1972 года горизонтальных лифтов больше не строили, этот проект через три года был замёнен на более удачный: односводчатые станции ленинградского типа.\n\r\n    </p>\r\n    <p>\r\n      \nСтанционные стены облицованы белым мрамором «коелга» и дополнены металлическим карнизом с рисунком в виде вертикальных полос. Художественное оформление станции посвящено освоению космического пространства: решётка в торце центрального зала украшена символическим изображением нескольких звёзд с четырьмя лучами.\n\r\n    </p>\r\n  </div>\r\n</div>\r\n', 2);
